mapper.update:
update order_form
user_id = #{userId}, product_id = #{productId}, shop_id = #{shopId}, order_username = #{orderUsername}, order_phone = #{orderPhone}, order_address = #{orderAddress}, order_user_remark = #{orderUserRemark}, order_shop_remark = #{orderShopRemark}, order_status = #{orderStatus}, create_time = #{createTime}, last_edit_time = #{lastEditTime}
where order_id = #{orderId};
