mapper.update:
update product
product_name = #{productName}, product_desc = #{productDesc}, img_path = #{imgPath}, normal_price = #{normalPrice}, promotion_price = #{promotionPrice}, priority = #{priority}, last_edit_time = #{lastEditTime}, enable_status = #{enableStatus}, product_category_id = #{productCategoryId},
where product_id = #{productId};

update product
set img_path = null
where product_id = #{productId}

mapper.select:
select product_id, product_name, product_desc, normal_price, promotion_price, priority, last_edit_time, enable_status from product
and product_category_id = #{product.productCategoryId}
and product_name like '%${product.productName}%'
and enable_status = #{product.enableStatus}
and shop_id = #{product.shopId}
order by priority desc ,enable_status desc limit #{rowIndex},#{pageSize};
